angular.module("tgFullCover", []),
angular.module("tgFullCover").directive("tgFullCover", [function() {
    return {
        restrict:"E", replace:!0, transclude:!0, scope: {
            arrow: "=", omitContainer: "="
        }
        , link:function(a, b, c) {
            var d=c.color||"a";
            a.colorClass="h-bg-"+d, a.arrowColor="arrow-"+d;

        }
        , template:'<section role="region" class="full-cover {{colorClass}}" ng-class="{\'arrow-container\': arrow }"><div ng-class="{container:!omitContainer}" ng-transclude></div><div ng-if="arrow" class="arrow arrow-down {{arrowColor}} arrow-marker"></div></section>'
    }
}

]);